ration // segment_duration) return segment_duration, max_segments else: print(f\\"La durée doit être un nombre positif et inférieure ou égale à {allowable_duration} secondes.\\") except ValueError: print(\\"Veuillez entrer un nombre valide.\\") def ask_directory_preference(): print(\\"Souhaitez-vous inclure les sous-dossiers dans la recherche des vidéos ?\\") print(\\"1- Oui\\") print(\\"2- Non\\") choice = input(\\"Veuillez entrer le numéro de votre choix (1 ou 2) : \\") return choice.strip() == '1' # Retourne True si l'utilisateur choisit '1' (Oui), False sinon def calculate_loudness(audio_data): if audio_data.ndim == 1: volume = audio_data.astype('float32') ** 2 else: volume = np.mean(audio_data.astype('float32') ** 2, axis=1) volume_dB = 10 * np.log10(volume + 1e-9) # +1e-9 to avoid log(0) and convert to dB return volume_dB def find_loudest_moments(audio_filename, num_moments, segment_duration, video_duration, starting_offset, ending_offset): try: # Lire les données audio depuis le fichier rate, audio_data = wavfile.read(audio_filename) if audio_data.ndim == 2: audio_data = np.mean(audio_data, axis=1) volume_dB = calculate_loudness(audio_data) segment_half_duration = segment_duration / 2.0 start_index = int(starting_offset * rate) end_index = int((video_duration - ending_offset) * rate) moments = [] volumes = [] # While there are less moments found than requested and there is still data to analyze while len(moments) < num_moments and (end_index - start_index) > int(segment_duration * rate): index = np.argmax(volume_dB[start_index:end_index]) moment = (start_index + index) / rate moment_volume = volume_dB[start_index + index] # Move the start_index past the recently found moment start_index += int(segment_duration * rate) if start_index > end_index: break # Break the loop if we have run out of audio data to consider # Suppress loudness of the recently found moment suppression_start = max(start_index - int(segment_half_duration * rate), 0) suppression_end = min(st