ngitude = 13.4050; // Datum, f√ºr das die Sonnenposition berechnet werden soll int year = 2022; int month = 10; int day = 1; int hour = 12; // Uhrzeit in 24-Stunden-Format int minute = 0; int second = 0; // Berechne die Anzahl der Tage seit J2000.0 (1. Januar 2000, 12:00 Uhr UTC) int a = (14 - month) / 12; int y = year + 4800 - a; int m = month + 12 * a - 3; double jdn = day + (153 * m + 2) / 5 + 365 * y + y / 4 - y / 100 + y / 400 - 32045.5; // Berechne die Anzahl der Jahrhunderte seit J2000.0 double t = (jdn - 2451545.0) / 36525; // Berechne die ekliptikale L√§nge der Sonne double l = 280.460 + 0.9856474 * jdn; l %= 360; if (l < 0) { l += 360; } // Berechne die mittlere Anomalie der Sonne double m1 = 357.528 + 0.9856003 * jdn; m1 %= 360; if (m1 < 0) { m1 += 360; } // Berechne die ekliptikale L√§nge der Sonne double mr = 1.915 * Math.Sin(Math.PI * m1 / 180) + 0.020 * Math.Sin(Math.PI * 2 * m1 / 180); double lambda = l + mr; // Berechne die ekliptikale Breite der Sonne double eps = 23.439 - 0.0000004 * jdn; double beta = 0; // Berechne die Rektaszension und Deklination der Sonne double alpha = Math.Atan2(Math.Cos(Math.PI * beta / 180) * Math.Sin(Math.PI * lambda / 180), Math.Cos(Math.PI * lambda / 180)) * 180 / Math.PI; double delta = Math.Asin(Math.Sin(Math.PI * beta / 180) * Math.Sin(Math.PI * lambda / 180)) * 180 / Math.PI; // Berechne die Stundenwinkel der Sonne double ha = Math.PI * (hour + (minute + second / 60.0) / 60.0 - 12 + longitude / 15.0) / 12; double x = Math.Cos(Math.PI * delta / 180) * Math.Cos(ha); double y = Math.Cos(Math.PI * delta / 180) * Math.Sin(ha); double z = Math.Sin(Math.PI * delta / 180); double azimuth = Math.Atan2(Math.Sin(ha), Math.Cos(ha) * Math.Sin(Math.PI * latitude / 180) - Math.Tan(Math.PI * delta / 180) * Math.Cos(Math.PI * latitude / 180)) * 180 / Math.PI; double altitude = Math.Asin(z * Math.Sin(Math.PI * latitude / 180) + Math.Cos(Math.PI * latitude / 180) * Math.Sqrt(1 - Math.Pow(z, 2))) * 180 / Math.PI; Console.WriteLine(\\"