ls.JSON = JSON; res.render('search', { musicians, query, role, city, citiesAndRegions}); //res.redirect('/search'); }); app.get(\\"/profile/:id/edit\\", requireLogin, (req, res) => { const musician = getMusicianById(parseInt(req.params.id)); if (musician) { if (req.session.musicianId === musician.id) { // Check if the logged-in user is the owner of the profile res.render(\\"edit-profile\\", { musician: musician }); } else { res.status(403).send(\\"Access denied\\"); } } else { res.status(404).send(\\"Musician not found\\"); } }); app.post('/profile/:id/edit', requireLogin, (req, res) => { const musician = getMusicianById(parseInt(req.params.id)); if (musician) { if (!req.body.name || !req.body.genre) { res.status(400).send('Please fill out all fields'); } else { musician.name = req.body.name; musician.genre = req.body.genre; musician.instrument = req.body.instrument; musician.soundcloud = req.body.soundcloud; musician.soundcloud1 = req.body.soundcloud1; musician.soundcloud2 = req.body.soundcloud2; musician.location = req.body.location; musician.role = req.body.role; musician.bio = req.body.bio; if (req.files && req.files.thumbnail) { const file = req.files.thumbnail; const filename = 'musician_' + musician.id + '_' + file.name; file.mv('./public/img/' + filename); musician.thumbnail = filename; } const data = fs.readFileSync('./db/musicians.json'); const musicians = JSON.parse(data); const index = musicians.musicians.findIndex(m => m.id === musician.id); musicians.musicians[index] = musician; fs.writeFileSync('./db/musicians.json', JSON.stringify(musicians)); res.redirect('/profile/' + musician.id); } } else { res.status(404).send('Musician not found'); } }); function isValidSoundCloudUrl(url) { return url.startsWith('https://soundcloud.com/'); } app.listen(3000, () => { console.log(\\"Server started on port 3000\\"); }); index.ejs: <!DOCTYPE html> <html lang=\\"en\\"> <head> <meta charset=\\"UTF-8\\" /> <meta name=\\"viewport\\" content=\\"width=device-width, initi